import org.junit.*;
import java.util.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(.+) is at (\\d+), (\\d+)$")
    public void personIsAt(String name, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }

    @Given("^people are located at$")
    public void peopleAt(List<List<String>> dataTable) throws Throwable {
        for(List<String> values : dataTable) {
           shouty.setLocation(values.get(0), new Coordinate(Integer.parseInt(values.get(1)), Integer.parseInt(values.get(2))));
        }
    }


    @When("^(.+) shouts$")
    public void shouterShouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }

    @Then("^(.+) should hear (.+)$")
    public void listenerShouldHearShouter(String name, String shouterName) throws Throwable {
        Map<String, List<String> > shouts = shouty.getShoutsHeardBy(name);
        List<String> shoutsByShouter = shouts.get(shouterName);
        assertNotNull(String.format("%s couldn't hear %s, instead heard these shouters: %s", name, shouterName, shouts.entrySet()), 
                                           shoutsByShouter);
        assertEquals(String.format("%s couldn't hear %s, instead heard these shouters: %s", name, shouterName, shouts.entrySet()),
                            1, shoutsByShouter.size());
    }

    @Then("^(.+) should hear (\\d)+ shouts from (.+)$")
    public void listenerShouldHearFromSameShouter(String name, int count, String shouterName) throws Throwable {
        Map<String, List<String> > shouts = shouty.getShoutsHeardBy(name);
        List<String> shoutsByShouter = shouts.get(shouterName);
        assertNotNull(String.format("%s couldn't hear %s %d times, instead heard these shouters: %s", name, shouterName, count,  shouts.entrySet()), 
                                           shoutsByShouter);
        assertEquals(String.format("%s couldn't hear %s %d times, instead heard these shouters: %s", name, shouterName, count,  shouts.entrySet()), 
                            count, shoutsByShouter.size());
    }

    @Then("^(.+) should not hear (.+)$")
    public void lucy_should_hear_nothing(String name, String shouterName) throws Throwable {
        Map<String, List<String> > shouts = shouty.getShoutsHeardBy(name);
        List<String> shoutsByShouter = shouts.get(shouterName);
        assertNull(String.format("%s could hear %s, %s heard these shouters: %s", name, shouterName, name, shouts.entrySet()),
                          shoutsByShouter);
    }


}

