import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import java.util.*;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(.+) is at (\\d+), (\\d+)$")
    public void personIsAt(String personName, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(personName, new Coordinate(xCoord, yCoord));
    }

    @When("^(.+) shouts$")
    public void seanShouts(String shouter) throws Throwable {
        shouty.shout(shouter, ARBITRARY_MESSAGE);
    }

    @Then("^(.+) should not hear (.+)$")
    public void lucyShouldNotHearOscar(String listener, String shouter) throws Throwable {
    Set<String> names = shouty.getShoutsHeardBy(shouter).keySet();
    boolean lucyheardOscar = names.contains(listener);
    Assert.assertFalse(lucyheardOscar);
    }

    @Then("^(.+) should hear (.+)$")
    public void lucyShouldHearSean(String listener, String shouter) throws Throwable {
    Set<String> names = shouty.getShoutsHeardBy(shouter).keySet();
    boolean lucyheardSean = names.contains(listener);
    Assert.assertTrue(lucyheardSean);
    }

    @Given("^people are located at:$")
    public void peopleAreLocatedAt(List<PersonLocation> whereabouts) throws Throwable {
        for(PersonLocation personLocation : whereabouts)
        {
            personIsAt(personLocation.name, personLocation.x , personLocation.y);
        }
    }
}

