import java.util.Set;
import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(.+) is at (\\d+), (\\d+)$")
    public void personIsAt(String personName, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(personName, new Coordinate(xCoord, yCoord));
    }

    @When("^(.+) shouts$")
    public void shouterShouts(String personName) throws Throwable {
        shouty.shout(personName, ARBITRARY_MESSAGE);
    }

    @Then("^(.*) should hear (.*)$")
    public void listenerShouldHearShouter(String listenerName, String shouterName) throws Throwable {
        Set<String> shouterNames = shouty.getShoutsHeardBy(listenerName).keySet();
        Assert.assertTrue(shouterNames.contains(shouterName));
    }

    @Then("^(.*) should not hear (.*)$")
    public void listenerShouldNotHearShouter(String listenerName, String shouterName) throws Throwable {
        Set<String> shouterNames = shouty.getShoutsHeardBy(listenerName).keySet();
        Assert.assertFalse(
            String.format("Did not expect %s to hear %s, but heard these people: %s", listenerName, shouterName, shouterNames), 
            shouterNames.contains(shouterName));
    }
}

