public class Coordinate {
  private int x;
  private int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  public int distanceFrom(Coordinate other){
      int absx = x - other.x;
      int absy = y - other.y;
      double distance = Math.sqrt((absx * absx) + (absy * absy));
      return (int) distance;
  }
}

