import org.junit.*;
import java.util.List;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(\\w+) is at ([-]*\\d+), ([-]*\\d+)$")
    public void userAt(String name, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }

    @Given("^people are located at$")
    public void peopleAt(List <Personlocation> personLocations) throws Throwable {
        for (Personlocation location : personLocations) {
        shouty.setLocation(location.name, new Coordinate(location.x, location.y));
        }
    }

    @When("^(\\w+) shouts$")
    public void seanShouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }

    @Then("^(\\w+) should hear (\\w+)$")
    public void listenerShouldHearHim(String listener, String shouter) throws Throwable {
        Assert.assertTrue(
            String.format("%s should have heard %s", listener, shouter),
            shouty.getShoutsHeardBy(listener).containsKey(shouter));
    }

    @Then("^(\\w+) should not hear (\\w+)$")
    public void listenerShouldNotHearHim(String listener, String shouter) throws Throwable {
        Assert.assertFalse(
             String.format("%s should not have heard %s", listener, shouter),
            shouty.getShoutsHeardBy(listener).containsKey(shouter));
    }

}

class Personlocation {
 String name;
 int x;
 int y;
 }

