module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {

    var xDiff = Math.abs(this.x - other.x)
    var yDiff = Math.abs(this.y - other.y)

    return Math.sqrt(Math.pow(xDiff,2) + Math.pow(yDiff,2))

  }
};

