var {Before, Given, When, Then } = require('cucumber')
var assert = require('assert')
var Shouty = require('../shouty')
var Coordinate = require('../coordinate')

const ARBITARY_MESSAGE = 'Hello, world'
let shouty

Before(function() {
  shouty = new Shouty()
})

Given('{word} is at {int}, {int}', function (name, x, y) {
  shouty.setLocation(name, new Coordinate(x, y))
})

Given('people are located at', function (dataTable) {
   dataTable.hashes().forEach (row => {
     shouty.setLocation(row.name, new Coordinate(row.x, row.y))
   })
 });

When('{word} shouts', function (name) {
  shouty.shout(name, ARBITARY_MESSAGE)
})

Then('{word} should hear {word}', function (receiver, shouter) {
  shoutsHeard = Object.keys(shouty.getShoutsHeardBy(receiver))
  assert.equal(true, shoutsHeard.includes(shouter))
})

Then('{word} should hear no thing', function (receiver) {
  assert.equal(Object.keys(shouty.getShoutsHeardBy(receiver)).length, 0)
})

Then('{word} should not hear {word}', function (receiver, shouter) {
  shoutsHeard = Object.keys(shouty.getShoutsHeardBy(receiver))
  assert.equal(false, shoutsHeard.includes(shouter))
})

Then('{word} should hear {int} shouts from {word}', function (receiver, shoutcount, shouter) {
  shouts = shouty.getShoutsHeardBy(receiver)
  console.log(shouts)
  //console.log(shoutsHeard.length)
  assert.equal(true, shouts[shouter] != undefined)
  assert.equal(shoutcount, shouts[shouter].length)
});

