shouty.js:
module.exports = function Shouty() {
  const MESSAGE_RANGE = 1000
  this.locations = {}
  this.shouts = {}
  this.dndState = {}
  this.dndStateForShouter = {}

  this.setLocation = function(person, coordinate) {
    this.locations[person] = coordinate
  }

  this.setDNDState = function(person, state) {
    this.dndState[person] = state;
  }

  this.setDNDStateForShouter = function(person, shouter, state) {
    if(this.dndStateForShouter[person] == undefined) {
      this.dndStateForShouter[person] = {}
    }

    var t = this.dndStateForShouter[person]
    t[shouter] = state

  }

  this.shout = function(person, shout) {
    if(!(person in this.shouts)) {
      this.shouts[person] = []
    }
    this.shouts[person].push(shout)
  }

  this.getShoutsHeardBy = function(listener) {
    var shoutsHeard = {}
    var dndForShouter = this.dndStateForShouter[listener]

    Object.keys(this.shouts).forEach(shouter => {
        var personsShouts = this.shouts[shouter]
        var distance = this.locations[listener].distanceFrom(this.locations[shouter])
        if(distance < MESSAGE_RANGE && shouter != listener && 
           (this.dndState[listener] == undefined || this.dndState[listener] == 'off') && 
           (dndForShouter == undefined || dndStateForShouter[shouter] == 'off'))
        {
          shoutsHeard[shouter] = personsShouts
        }
    })

    return shoutsHeard
  }
}

