module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {
    var xDist = Math.abs(this.x - other.x)
    var yDist = Math.abs(this.y - other.y)
    return Math.sqrt(Math.pow(xDist, 2) + Math.pow(yDist, 2));
  }
};

