var {Before, Given, When, Then } = require('cucumber')
var assert = require('assert')
var Shouty = require('../shouty')
var Coordinate = require('../coordinate')

const ARBITARY_MESSAGE = 'Hello, world'
let shouty

Before(function() {
  shouty = new Shouty()
})

Given('{word} is at {int}, {int}', function (name, x, y) {
  shouty.setLocation(name, new Coordinate(x, y))
})

Given('people are located at', function (dataTable) {
  dataTable.hashes().forEach(row => {
    shouty.setLocation(row.name, new Coordinate(row.x, row.y))
  })
})

When('{word} shouts', function (name) {
  shouty.shout(name, ARBITARY_MESSAGE)
})

Then('{word} should hear nothing', function (listener) {
  assert.equal(Object.keys(shouty.getShoutsHeardBy(listener)).length, 0)
})

Then('{word} should not hear Oscar', function (listener) {
  // Assumes listener can hear Sean
  assert.equal(Object.keys(shouty.getShoutsHeardBy(listener)).length, 1) 
})

Then('{word} should hear Sean', function (listener) {
  assert.equal(Object.keys(shouty.getShoutsHeardBy(listener)).length, 1) 
})

Then('{word} should not hear themselves', function (listener) {
  assert.equal(Object.keys(shouty.getShoutsMadeBySelf()).length, 0)
})

Then('{word} should hear {int} shouts from Sean', function (listener, int) {
  console.log(shouty.getShoutsHeardBy(listener))[Sean]
  assert.equal(shouty.getShoutsHeardBy(listener).length, int)
})

shouty.js:
module.exports = function Shouty() {
  const MESSAGE_RANGE = 1000
  this.locations = {}
  this.shouts = {}

  this.setLocation = function(person, coordinate) {
    this.locations[person] = coordinate
  }

  this.shout = function(person, shout) {
    if(!(person in this.shouts)) {
      this.shouts[person] = []
    }
    this.shouts[person].push(shout)
  }

  this.getShoutsHeardBy = function(listener) {
    var shoutsHeard = {}

    Object.keys(this.shouts).forEach(shouter => {
        var personsShouts = this.shouts[shouter]
        var distance = this.locations[listener].distanceFrom(this.locations[shouter])

        if(distance < MESSAGE_RANGE) {
          shoutsHeard[shouter] = personsShouts
        }
    })

    return shoutsHeard
  }

  this.getShoutsMadeBySelf = function() {
    var shoutsHeard = {}  
    return shoutsHeard = {}
  }
}

