module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {
    x_delta = Math.pow(other.x - this.x, 2)
    y_delta = Math.pow(other.y - this.y, 2)
    return Math.sqrt(x_delta + y_delta);
  }
};

