var {Before, Given, When, Then } = require('cucumber')
var assert = require('assert')

const ARBITARY_MESSAGE = 'Hello, world'


When('{word} shouts', function (name) {
  this.shouty.shout(name, ARBITARY_MESSAGE)
})

Then('{word} should hear Sean', function (name) {
  assert.equal(Object.keys(this.shouty.getShoutsHeardBy(name)).length, 1)
})

Then('{word} should hear nothing', function (name) {
  assert.equal(Object.keys(this.shouty.getShoutsHeardBy(name)).length, 0)
})

Then('{word} should not hear {word}', function (receiver, shouter) {
  shoutsHeard = Object.keys(this.shouty.getShoutsHeardBy(receiver))
  assert.equal(false, shoutsHeard.includes(shouter))
});

Then('{word} should hear {int} shouts from {word}', function (receiver, numberOfShouts, shouter) {
  shoutsHeardFromShouter = this.shouty.getShoutsHeardBy(receiver)[shouter]
  assert.equal(numberOfShouts, shoutsHeardFromShouter.length)
});

