var assert = require('assert')
var Shouty = require('../shouty.js')
var Coordinate = require('../coordinate')

describe('shouty class', () => {
  it('person should not hear their own shout', () => {
    var person = 'Sean'
    var shouty = new Shouty()
    shouty.setLocation(person, new Coordinate(0, 0))
    shouty.shout(person, 'Hello world')
    shoutsHeard = Object.keys(shouty.getShoutsHeardBy(person))
    assert.equal(false, shoutsHeard.includes(person))
  })
})

