module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {
    var xDelta = this.x - other.x
    var yDelta = this.y - other.y
    return Math.abs(Math.sqrt(xDelta*xDelta + yDelta*yDelta))
  }
};

