coordinate.js:
module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {
    var xDelta = this.x - other.x
    var yDelta = this.y - other.y
    return Math.abs(Math.sqrt(xDelta*xDelta + yDelta*yDelta))
  }
};

tests/coordinate.test.js:
var assert = require('assert')
var Coordinate = require('../coordinate')

describe("coordinate class", () => {

  it("should calculate the distance from itself", () => {
    var a = new Coordinate(0, 0)
    assert.equal(a.distanceFrom(a), 0)
  })

  it("should calculate the distance from another coordinate along X axis", ()=>{
    var a = new Coordinate(0, 0)
    var b = new Coordinate(600, 0)
    assert.equal(a.distanceFrom(b), 600)
  })

  it("should calculate the distance from another coordinate", ()=>{
    var a = new Coordinate(0, 0)
    var b = new Coordinate(300, 400)
    assert.equal(a.distanceFrom(b), 500)
  })

})

features/shout_steps.js:
const {When,Then} = require('cucumber');
const assert = require('assert');

const ARBITARY_MESSAGE = 'Hello, world';

When('{word} shouts', function (name) {
  this.shouty.shout(name, ARBITARY_MESSAGE)
});

Then('Lucy should hear Sean', function () {
  var heard = this.shouty.getShoutsHeardBy('Lucy')
  var shouters = Object.keys(heard)
  assert.equal(true, shouters.includes('Sean'))
});

Then('Lucy should hear nothing', function () {
  var heard = this.shouty.getShoutsHeardBy('Lucy')  
  var shouters = Object.keys(heard)
  assert.equal(shouters.length, 0)
});

Then('{word} should not hear {word}', function (listener, shouter) {
  var heard = this.shouty.getShoutsHeardBy(listener)
  var shouters = Object.keys(heard)
  assert.equal(false, shouters.includes(shouter))  
});

Then('Lucy should hear {int} shouts from Sean', function (n) {
  var heard = this.shouty.getShoutsHeardBy('Lucy')
  var shouters = Object.keys(heard)
  assert.equal(true, shouters.includes('Sean'))
  assert.equal(heard['Sean'].length, n)
});

shouty.js:
module.exports = function Shouty() {
  const MESSAGE_RANGE = 1000
  this.locations = {}
  this.shouts = {}

  this.setLocation = function(person, coordinate) {
    this.locations[person] = coordinate
  }

  this.shout = function(person, shout) {
    if (!(person in this.shouts)) {
      this.shouts[person] = []
    }
    this.shouts[person].push(shout)
  }

  this.getShoutsHeardBy = function(listener) {
    var heard = {}
    Object.keys(this.shouts).forEach(shouter => {
        var personsShouts = this.shouts[shouter]
        var distance = this.locations[listener].distanceFrom(this.locations[shouter])

        if (listener != shouter && distance < MESSAGE_RANGE) {
          heard[shouter] = personsShouts
        }
    })
    return heard
  }
}

