class Coordinate
  attr_reader :x, :y

  def initialize(x, y)
    @x = x
    @y = y
  end

  def distance_from(other)
    x_delta = (@x - other.x).abs
    y_delta = (@y - other.y).abs
    Math.sqrt((x_delta * x_delta) + (y_delta * y_delta))
  end
end

