using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace Shouty
{
    public class Coordinate
    {
        private readonly int xCoord;
        private readonly int yCoord;

        public Coordinate(int xCoord, int yCoord)
        {
            this.xCoord = xCoord;
            this.yCoord = yCoord;
        }

        public int DistanceFrom(Coordinate other)
        {
          // TODO: actually calculate distance beteen the coordinates.
              //  return Math.Abs(xCoord - other.xCoord);

            int dx = this.xCoord - other.xCoord;
            int dy = this.yCoord - other.yCoord;

            return (int)Math.Sqrt(dx*dx+dy*dy);

          //return 0;
        }
    }
}

