namespace Shouty.Specs
{
    [Binding]
    public class ShoutSteps
    {
        private const string ARBITRARY_MESSAGE = "Hello, world";
        private readonly ShoutyNetwork shouty = new ShoutyNetwork();

        [Given(@"(Lucy|Sean) is at (.*), (.*)")]
        public void GivenPersonIsAt(string person, int xCoord, int yCoord)
        {
            shouty.SetLocation(person, new Coordinate(xCoord, yCoord));
        }

         [Given(@"people are located at")]
        public void GivenPeopleAreLocatedAt(Table table)
        {
           foreach (var row in table.Rows)
           {
            string person = row["name"];
            int xCoord = (int) table.Rows["x"];
            int yCoord = (int) table.Rows["y"];
            shouty.SetLocation(person, new Coordinate(xCoord, yCoord));
           }
        }


