using System;
using TechTalk.SpecFlow;
using TechTalk.SpecFlow.Assist;
using Shouty;
using System.Collections.Generic;
using NUnit.Framework;

namespace Shouty.Specs
{
    [Binding]
    public class ShoutSteps
    {
        private const string ARBITRARY_MESSAGE = "Hello, world";
        private readonly ShoutyNetwork shouty = new ShoutyNetwork();

        [Given(@"(Lucy|Sean|Oscar) is at (\d*), (\d*)")]
        public void GivenSomebodyAt(string name, int x, int y)
        {
            shouty.SetLocation(name, new Coordinate(x, y));
        }

        [Given(@"people are location at")]
        public void GivenSomebodyAt(Table table)
        {
            var locations=table.CreateSet<Location>();

            foreach (var location in locations)
            {
                shouty.SetLocation(location.Name, new Coordinate(location.X, location.Y));
            }
        }

        [When(@"(Sean|Oscar|Lucy) shouts")]
        public void WhenSomebodyShouts(string name)
        {
            shouty.Shout(name, ARBITRARY_MESSAGE);
        }

        [Then(@"Lucy should hear Sean")]
        public void ThenLucyShouldHearSean()
        {
            Assert.AreEqual(1, shouty.GetShoutsHeardBy("Lucy").Count);
        }

        [Then(@"Lucy should hear nothing")]
        public void ThenLucyShouldHearNothing()
        {
            Assert.AreEqual(0, shouty.GetShoutsHeardBy("Lucy").Count);
        }

        [Then(@"Lucy should not hear Oscar")]
        public void ThenLucyShouldNotHearOscar()
        {
             Assert.AreEqual(1, shouty.GetShoutsHeardBy("Lucy").Count);
        }
    }
}

