var {Before, Given, When, Then } = require('cucumber')
var assert = require('assert')
var Shouty = require('../shouty')
var Coordinate = require('../coordinate')

const ARBITARY_MESSAGE = 'Hello, world'
let shouty

Before(function() {
  shouty = new Shouty()
})

Given('{word} is at {int}, {int}', function (name, x, y) {
  shouty.setLocation(name, new Coordinate(x, y))
})

Given('people are located at', function (dataTable) {
  dataTable.hashes().forEach(row => { 
    shouty.setLocation(row.name, new Coordinate(row.x, row.y)) 
  })
})

When('{word} shouts', function (name) {
  shouty.shout(name, ARBITARY_MESSAGE)
})

Then('{word} should hear {word}', function (name1, name2) {
  if (name2 == "nothing")
    assert.equal(Object.keys(shouty.getShoutsHeardBy(name1)).filter(s => s == name2).length, 0)
  else
    assert.equal(Object.keys(shouty.getShoutsHeardBy(name1)).filter(s => s == name2).length, 1)
})

Then('{word} should not hear {word}', function (name1, name2) {
  assert.equal(Object.keys(shouty.getShoutsHeardBy(name1)).filter(s => s == name2).length, 0)
})

Then('{word} should hear {int} shouts from {word}', function (name1, shoutNum, name2) {
  console.log(Object.keys(shouty.getShoutsHeardBy(name1)).filter(s => s == name2).length)
  assert.equal(shouty.getShoutsHeardBy(name1)).filter(s => s.key == name2).length, shoutNum)
});

