module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.xFrom = x => Math.abs(x, this.x);
  this.yFrom = y => Math.abs(y, this.y);
  this.distanceFrom = ({x, y}) => Math.sqrt(
    Math.pow(this.xFrom(x), 2) + Math.pow(this.yFrom(y), 2)
  );
};

