const { setWorldConstructor, defineParameterType } = require('cucumber')

var Shouty = require('../shouty');
var Coordinate = require('../coordinate');

function CustomWorld() {
  this.shouty = new Shouty();

  this.setLoc = (n, xOrCoord, y) => {
    if (y !== undefined) xOrCoord = new Coordinate(xOrCoord,y);
    this.shouty.setLocation(n, xOrCoord);
  }
}

setWorldConstructor(CustomWorld)

defineParameterType({
  regexp: /(\d+), (\d+)/,
  transformer: (x, y) => new Coordinate(parseInt(x), parseInt(y)),
  name: 'coordinate',
});

