module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {
    var xdelta = Math.abs(this.x - other.x)
    var ydelta = Math.abs(this.y - other.y)
    return Math.sqrt( xdelta * xdelta + ydelta * ydelta)
  }
};

