var {Before, Given, When, Then } = require('cucumber')
var assert = require('assert')
var Shouty = require('../shouty')
var Coordinate = require('../coordinate')

const ARBITARY_MESSAGE = 'Hello, world'
let shouty

Before(function() {
  shouty = new Shouty()
})

Given('{word} is at {int}, {int}', function (personName, x, y) {
  shouty.setLocation(personName, new Coordinate(x, y))
})

When('{word} shouts', function (personName) {
  shouty.shout(personName, ARBITARY_MESSAGE)
})

Then('{word} should not hear {word}', function (target, shouter) {
  const lucy = shouty.getShoutsHeardBy(target)
  assert.equal(lucy[shouter], undefined)
})

Then('{word} should hear {word}', function (target, shouter) {
  const lucy = shouty.getShoutsHeardBy(target)
  assert.equal(!lucy[shouter], false)
})

Then('{word} should not hear any shouts', function (target) {
  assert.equal(Object.keys(shouty.getShoutsHeardBy(target)).length, 0)
})

Then('{word} should not hear own shouts', function (shouter) {
  const targets = shouty.getShoutsHeardBy(shouter)
  //{ Lucy: [ 'Hello, world' ] } 'Lucy'
  assert.equal(targets[shouter], undefined)
});

shouty.js:
module.exports = function Shouty() {
  const MESSAGE_RANGE = 1000
  this.locations = {}
  this.shouts = {}

  this.setLocation = function(person, coordinate) {
    this.locations[person] = coordinate
  }

  this.shout = function(person, shout) {
    console.log('shout[person]', shout[person], person, 'sh', shout)
    if (shout[person]!==undefined) {
      console.log('shouter should not hear own shouts')
      return 
    }

    if(!(person in this.shouts)) {
      this.shouts[person] = []
    }


    this.shouts[person].push(shout)
  }

  this.getShoutsHeardBy = function(listener) {
    var shoutsHeard = {}

    Object.keys(this.shouts).forEach(shouter => {
        var personsShouts = this.shouts[shouter]
        var distance = this.locations[listener].distanceFrom(this.locations[shouter])

        if(distance < MESSAGE_RANGE) {
          shoutsHeard[shouter] = personsShouts
        }
    })

    return shoutsHeard
  }
}

