module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {

    return Math.sqrt((this.x - other.x)*(this.x - other.x) + (this.y -other.y)*(this.y -other.y));

  }
};

