public class Coordinate {
  private int x;
  private int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  public int distanceFrom(Coordinate other){
    double xDiff = (double) Math.pow(x - other.x, 2);
    double yDiff = (double) Math.pow(y - other.y, 2);

    return (int) Math.sqrt(xDiff + yDiff);
  }
}

