public class ShoutSteps {
    private static final String ARBITRARY_MESSAGE = "Hello, world";
    @Autowired
    private ShoutyHelper shouty;

    @When("{word} shouts")
    public void individual_shouts(String individual) {
        shouty.shout(individual, ARBITRARY_MESSAGE);
    }

    @Then("{word} hears {word}")
    public void should_hear(String shoutee, String shouter) {
        Map<String, List<String>> shoutsHeardByShouter = shouty.getShoutsHeardBy(shoutee);

        if(shouter.equals("nobody"))
            assertTrue(shoutsHeardByShouter.size() == 0);
        else {
            assertTrue(shoutsHeardByShouter.get(shouter) != null);
            assertTrue(shoutsHeardByShouter.get(shouter).size() > 0);
        }
    }

    @Then("{word} shouldn't hear {word}")
    public void shouldTHear(String shoutee, String shouter) {
        // Write code here that turns the phrase above into concrete actions
        Map<String, List<String>> shoutsByShouter = shouty.getShoutsHeardBy(shoutee);
        assertEquals(null, shoutsByShouter.get(shouter));
    }

    @Then("{word} hears {word} 1 time")
    public void hearsOneShout( String listener, 
                               String shouter) {
        hearsNumShouts(listener, shouter, 1);
    }

    @Then("{word} hears {word} {int} times")
    public void hearsNumShouts( String listener, 
                                String shouter,
                                Integer shoutCount) {
        Map<String, List<String>> shoutsHeardByListener = shouty.getShoutsHeardBy(listener);

        if (shoutCount == 0) {
            assertTrue(shoutsHeardByListener.get(shouter) == null);
            return;
        }

        assertTrue(shoutsHeardByListener.get(shouter) != null);
        assertTrue(shoutCount == shoutsHeardByListener.get(shouter).size());
    }
}

