import io.cucumber.datatable.DataTableType;
import io.cucumber.datatable.TableEntryTransformer;

import java.util.Locale;

import static java.lang.Integer.parseInt;

public class ShoutyTypes implements cucumber.api.TypeRegistryConfigurer {
    @Override
    public Locale locale() {
        return Locale.ENGLISH;
    }

    @Override
    public void configureTypeRegistry(TypeRegistry typeRegistry) {
        typeRegistry.defineDataTableType(
            new DataTableType(
                PersonLocation.class,
                (TableEntryTransformer<PersonLocation>) row -> {
                    return new PersonLocation(row.get("name"),
                                              Integer.parseInt(row.get("x")),
                                              Integer.parseInt(row.get("y")));
                }
            )
        );
    }
}

