import cucumber.api.java.en.Given;
import cucumber.api.java.en.Then;
import cucumber.api.java.en.When;

import org.springframework.beans.factory.annotation.Autowired;

import java.util.*;
import java.util.Collections.*;

public class LocationSteps {
    @Autowired
    private ShoutyHelper shouty;

    @Given("{word} is at {int}, {int}")
    public void person_is_at(String individual, int xCoord, int yCoord) {
        shouty.setLocation(individual, new Coordinate(xCoord, yCoord));
    }

    @Given("people located at")
    public void peopleLocatedAt(List<PersonLocation> locations) {
        for (PersonLocation location : locations) {
            person_is_at(location.name(),
                         location.x(),
                         location.y());
        }
    }
}

