import cucumber.api.java.en.Given;
import cucumber.api.java.en.Then;
import cucumber.api.java.en.When;
import java.util.*;

import static java.util.Collections.emptyMap;
import static org.junit.Assert.*;



public class ShoutSteps {
    private static final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("{word} is at {int}, {int}")
    public void lucy_is_at(String name, int xCoord, int yCoord) {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }

    @Given("people are located at")
    public void peopleAreLocatedAt(List<PersonLocation> locations) {
        for (PersonLocation location : locations) {
            shouty.setLocation(location.getName(), new Coordinate(location.getX(), location.getY()));
        }   
    }

    @When("{word} shouts")
    public void sean_shouts(String name) {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }

    @Then("{word} should hear {word}")
    public void receiver_should_hear_sender(String receiver, String sender) {
        if (sender.equals("nothing")) {
            assertEquals(emptyMap(), shouty.getShoutsHeardBy(receiver));
        } else {
            assertTrue(shouty.getShoutsHeardBy(receiver).containsKey(sender));
        }
    }

    @Then("{word} should not hear {word}")
    public void receiver_should_not_hear_sender(String receiver, String sender) {
        assertFalse(shouty.getShoutsHeardBy(receiver).containsKey(sender));
    }
}

class PersonLocation {
    private String name;
    private int x;
    private int y;

    public PersonLocation(String name, int x, int y) {
        this.name = name;
        this.x = x;
        this.y = y;
    }

    public String getName() {
        return name;
    }

    public int getX() {
        return x;
    }

    public int getY() {
        return y;
    }
}

