    //
    // For other transformations you can register a DataTableType.
      for (PersonLocation personLocation : personLocations) {
        shouty.setLocation(personLocation.name, personLocation.coordinate);
      }
    }

    @Given("{word} has an account")
    public void has_account(String name) {
        shouty.setLocation(name, new Coordinate(0, 0));
    }

    @Given("{word} is at {int}, {int}")
    public void person_is_at(String name, int xCoord, int yCoord) {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }

    @When("{word} shouts")
