      assertFalse(String.format("Heard the following: %s", 
         shouty.getShoutsHeardBy(listener)),
         shouty.getShoutsHeardBy(listener).containsKey(shouter)
      );
    }

    @Then("Lucy should hear no shouts")
    public void lucy_should_hear_nothing() {
      assertEquals(emptyMap(), shouty.getShoutsHeardBy("Lucy"));
    }

}

