    }

    @When("^they spell (\\d+) times (\\d+)$")
    public void theySpellTimes(int arg1, int arg2) throws Throwable {
        answer = Hiker.answer(arg1,arg2);
    }

    @Then("^the score is (\\d+)$")
    public void theScoreIs(int expected) throws Throwable {
        assertEquals(expected, answer);
    }
