using System;

namespace Shouty
{
    public class Coordinate
    {
        private readonly int xCoord;
        private readonly int yCoord;

        public Coordinate(int xCoord, int yCoord)
        {
            this.xCoord = xCoord;
            this.yCoord = yCoord;
        }

        public int DistanceFrom(Coordinate other)
        {
            int xDelta = xCoord - other.xCoord;
            int yDelta = yCoord - other.yCoord;
            return (int)Math.Abs(Math.Sqrt(xDelta*xDelta + yDelta*yDelta));
        }
    }
}

