using System;
using TechTalk.SpecFlow;
using TechTalk.SpecFlow.Assist;
using Shouty;
using NUnit.Framework;

namespace Shouty.Specs
{
    [Binding]
    public class ShoutSteps
    {
        private const string ARBITRARY_MESSAGE = "Hello, world";

        private readonly ShoutyNetwork shouty;

        public ShoutSteps(ShoutyNetwork di)
        {
            shouty = di;
        }

        [When(@"(Lucy|Sean|Oscar) shouts")]
        public void When_Person_Shouts(string name)
        {
            shouty.Shout(name, ARBITRARY_MESSAGE);
        }

        [Then(@"Lucy should hear (.*) shouts from Sean")]
        public void Then_Lucy_Should_Hear_N_Shouts_From_Sean(int n)
        {
            var shouts = shouty.GetShoutsHeardBy("Lucy");
            Assert.AreEqual(n, shouts["Sean"].Count);
        }

        [Then(@"Lucy should hear Sean")]
        public void Then_Lucy_Should_Hear_Sean()
        {
            var shouts = shouty.GetShoutsHeardBy("Lucy");
            Assert.IsTrue(shouts.ContainsKey("Sean"));
        }

        [Then(@"([^ ]*) should not hear ([^ ]*)")]
        public void Then_Lucy_Should_Not_Hear_Oscar(string listener, string shouter)
        {
            var shouts = shouty.GetShoutsHeardBy(listener);            
            Assert.IsFalse(shouts.ContainsKey(shouter));            
        }        

        [Then(@"Lucy should hear nothing")]
        public void Then_Lucy_Should_Hear_Nothing()
        {
            Assert.AreEqual(0, shouty.GetShoutsHeardBy("Lucy").Count);
        }
    }
}

