using TechTalk.SpecFlow;
using TechTalk.SpecFlow.Assist;
using Shouty;

namespace Shouty.Specs
{
    [Binding]
    public class LocationSteps
    {
        private readonly ShoutyNetwork shouty;

        public LocationSteps(ShoutyNetwork di)
        {
            shouty = di;
        }

        [Given(@"(Lucy|Sean|Oscar) is at (\d+), (\d+)")]
        public void Given_Person_Is_At(string name, int xCoord, int yCoord)
        {
            shouty.SetLocation(name, new Coordinate(xCoord, yCoord));
        }

        [Given(@"people are located at")]
        public void Given_People_Are_Located_At(Table table)
        {
            foreach (var row in table.CreateSet<LocationRow>())
            {
                shouty.SetLocation(row.Name, new Coordinate(row.X, row.Y));                
            }
        }        
    }
}

