using System;
using TechTalk.SpecFlow;
using TechTalk.SpecFlow.Assist;
using Shouty;
using NUnit.Framework;

namespace Shouty.Specs
{
    [Binding]
    public class ShoutSteps
    {
        private const string ARBITRARY_MESSAGE = "Hello, world";
        private readonly ShoutyNetwork shouty = new ShoutyNetwork();

        [Given(@"(Lucy|Sean|Oscar) is at (.*), (.*)")]
        public void GivenPersonIsAt(string person, int xCoord, int yCoord)
        {
            shouty.SetLocation(person, new Coordinate(xCoord, yCoord));
        }

//        [Given(@"Sean is at (.*), (.*)")]
//        public void GivenSeanIsAt(int xCoord, int yCoord)
//        {
//            shouty.SetLocation("Sean", new Coordinate(xCoord, yCoord));
//        }

        [When(@"(Sean|Oscar) shouts")]
        public void WhenPersonShouts(string person)
        {
            shouty.Shout(person, ARBITRARY_MESSAGE);
        }

        [Then(@"(Lucy|Sean|Oscar) should not hear (Sean|Oscar)")]
        public void ThenListenerShouldNotHearShouter(string listener, string shouter)
        {
            Assert.IsFalse(shouty.GetMessagesHeardBy(listener).ContainsKey(shouter));
        }

        [Then(@"Lucy should hear (Sean|Oscar)")]
        public void ThenLucyShouldHearPerson(string person)
        {
            Assert.IsTrue(shouty.GetMessagesHeardBy("Lucy").ContainsKey(person));
        }

        [Then(@"Lucy should hear nothing")]
        public void ThenLucyShouldHearNothing()
        {
            Assert.AreEqual(0, shouty.GetMessagesHeardBy("Lucy").Count);
        }

        public class Coordinates
        {
            public string name { get; set; }
            public string X { get; set; }
            public string Y { get; set; }
        }

        [Given(@"people are located at")]
        public void GivenPeopleAreLocatedAt(Table table)
        {
            var coordinates = table.CreateSet<Coordinates>();   
            foreach (var row in coordinates)
            {
                var person = row.name;
                int xCoord = Int32.Parse(row.X);
                int yCoord = Int32.Parse(row.Y);

                shouty.SetLocation(person, new Coordinate(xCoord, yCoord));
            }
        }
    }
}

