using System;
using TechTalk.SpecFlow;
using Shouty;
using NUnit.Framework;
using TechTalk.SpecFlow.Assist;

namespace Shouty.Specs
{
    [Binding]
    public class ShoutSteps
    {
        private const string ARBITRARY_MESSAGE = "Hello, world";
        private readonly ShoutyNetwork shouty = new ShoutyNetwork();

        [Given(@"(Lucy|Sean|Oscar) is at (\d*), (\d*)")]
        public void GivenIsAt(string person, int xCoord, int yCoord)
        {
            shouty.SetLocation(person, new Coordinate(xCoord, yCoord));
        }

        [When(@"(Lucy|Sean|Oscar) shouts")]
        public void WhenSomeoneShouts(string person)
        {
            shouty.Shout(person, ARBITRARY_MESSAGE);
        }

        [Then(@"(Lucy|Sean|Oscar) should hear (Lucy|Sean|Oscar|nothing)")]
        public void ThenPersonShouldHearSomething(string person,
                                                  string something)
        {
            Assert.IsTrue(
                something == "nothing" ?
                      0 == shouty.GetMessagesHeardBy(person).Count :
                      shouty.GetMessagesHeardBy(person).ContainsKey(something));
        }

        [Then(@"(Lucy|Sean|Oscar) should not hear (Lucy|Sean|Oscar)")]
        public void ThenPersonShouldNotHearSomeone(string person,
